import { ADD_ITEM, REMOVE_ITEM } from 'Actions'

import { initialState } from 'initialState'
let toUseState = {}

const Reducers = (state = initialState, action) => {
  toUseState = state
  switch (action.type) {
    case ADD_ITEM:
      toUseState['cart'].push(action.payload.checkout)
      console.log(toUseState['cart'])
      for (let i = 0; i < toUseState.product.sizes.length; i++) {
        if (toUseState.product.sizes[i].id == action.payload.checkout.sizeId) {
          for (let j = 0; j < toUseState.product.sizes[i].colors.length; j++) {
            if (toUseState.product.sizes[i].colors[j].name == action.payload.checkout.colorId) {
              toUseState.product.sizes[i].colors[j].quantity =
                toUseState.product.sizes[i].colors[j].quantity - action.payload.checkout.quantity
              break
            }
          }
          break
        }
      }
      console.log(toUseState)
      return { toUseState }

    case REMOVE_ITEM:
      toUseState['cart'].splice(action.payload.id - 1, 1)
      console.log(action.payload.id)
      console.log(toUseState['cart'])
      for (let i = 0; i < toUseState.product.sizes.length; i++) {
        if (toUseState.product.sizes[i].id == action.payload.size) {
          for (let j = 0; j < toUseState.product.sizes[i].colors.length; j++) {
            if (toUseState.product.sizes[i].colors[j].name == action.payload.color) {
              toUseState.product.sizes[i].colors[j].quantity =
                toUseState.product.sizes[i].colors[j].quantity + action.payload.quantity
              console.log(toUseState.product.sizes)
              break
            }
          }
          break
        }
      }
      console.log(toUseState)
      return { ...toUseState }
    default:
      return toUseState
  }
}
export default Reducers
