import { ADD_ITEM, REMOVE_ITEM } from 'Actions'

import { initialState } from 'initialState'

const Reducers = (state = initialState, action) => {
  let copyState = state
  switch (action.type) {
    case ADD_ITEM:
      copyState['cart'].push(action.payload.checkout)
      console.log(copyState['cart'])
      for (let i = 0; i < copyState.product.sizes.length; i++) {
        if (copyState.product.sizes[i].id == action.payload.checkout.sizeId) {
          for (let j = 0; j < copyState.product.sizes[i].colors.length; j++) {
            if (copyState.product.sizes[i].colors[j].name == action.payload.checkout.colorId) {
              copyState.product.sizes[i].colors[j].quantity =
                copyState.product.sizes[i].colors[j].quantity - action.payload.checkout.quantity
              break
            }
          }
          break
        }
      }
      return { copyState }

    case REMOVE_ITEM:
      state['cart'].splice(action.payload.id - 1, 1)
      console.log(action.payload.id)
      console.log(state['cart'])
      for (let i = 0; i < state.product.sizes.length; i++) {
        if (state.product.sizes[i].id == action.payload.size) {
          for (let j = 0; j < state.product.sizes[i].colors.length; j++) {
            if (state.product.sizes[i].colors[j].name == action.payload.color) {
              state.product.sizes[i].colors[j].quantity =
                state.product.sizes[i].colors[j].quantity + action.payload.quantity
              console.log(state.product.sizes)
              break
            }
          }
          break
        }
      }
      console.log(state)
      return { ...state }
    default:
      return state
  }
}
export default Reducers
